INSERT INTO [dbo].[Planets] ([Name], [GeneralInfo], [Category], [Location], [AvgDistanceFromSun], [OrbitalPeriod], [AvgOrbitalSpeed], [Inclination], [Satellites], [Radius], [SurfaceArea], [Mass], [Density], [RotationPeriod], [RotationSpeed], [AxialTilt], [AvgSurfaceTemp]) VALUES (N'Earth', N'Earth is the third planet in the solar system and the fifth largest in the system, and the largest of the four terrestrial planets. Earth is the only known celestial body that contains life forms.', N'Terrestrial planet', N'Inner Solar System', N'149,598,0236', N'365.25', N'29.783', N'0', N'1', N'6,378', N'510,065,600', N'5.9742×10^24', N'5.5153', N'23.93', N'0.4651', N'23.436', N'14')
INSERT INTO [dbo].[Planets] ([Name], [GeneralInfo], [Category], [Location], [AvgDistanceFromSun], [OrbitalPeriod], [AvgOrbitalSpeed], [Inclination], [Satellites], [Radius], [SurfaceArea], [Mass], [Density], [RotationPeriod], [RotationSpeed], [AxialTilt], [AvgSurfaceTemp]) VALUES (N'Jupiter', N'Jupiter is a gas giant and planet with the largest mass in the solar system, the fifth farthest from the sun and the first in the gaseous planet category. Jupiter is made mainly of gas: about 90% of its mass is hydrogen and about 10% is helium.', N'Gas giant', N'Outer Solar System', N'778,340,821', N'4,332.58', N'13.0697', N'1.30530', N'79', N'71,492', N'6.41×10^10', N'1.899×10^27', N'1.326', N'0.41', N'12.6', N'3.12', N'-121')
INSERT INTO [dbo].[Planets] ([Name], [GeneralInfo], [Category], [Location], [AvgDistanceFromSun], [OrbitalPeriod], [AvgOrbitalSpeed], [Inclination], [Satellites], [Radius], [SurfaceArea], [Mass], [Density], [RotationPeriod], [RotationSpeed], [AxialTilt], [AvgSurfaceTemp]) VALUES (N'Mars', N'Mars is the fourth planet in the solar system. Its orbit is the second closest to Earth''s orbit (after Venus) and is the second smallest planet, larger only than a hot star. The color of Mars'' face is reddish due to the abundance of iron oxides in its soil. On Mars is the highest mountain in the solar system, the volcano Olympus Mons - 27 km high.', N'Terrestrial planet', N'Inner Solar System', N'227,936,637', N'686.971', N'24.077', N'1.85061', N'2', N'3,396.2', N'1.448×10^8', N'6.4191×10^23', N'3.94', N'24.622', N'0.241', N'25.19', N'-63.15')
INSERT INTO [dbo].[Planets] ([Name], [GeneralInfo], [Category], [Location], [AvgDistanceFromSun], [OrbitalPeriod], [AvgOrbitalSpeed], [Inclination], [Satellites], [Radius], [SurfaceArea], [Mass], [Density], [RotationPeriod], [RotationSpeed], [AxialTilt], [AvgSurfaceTemp]) VALUES (N'Mercury', N'Mercury is the closest planet to the Sun. In terms of mass, it is the eighth and smallest planet in the solar system. It is much smaller than Earth, and similar in size to the Moon. Gravity on its surface is 2.5 times smaller than on Earth. The sunrise is visible on its surface once every two years - a unique phenomenon in the solar system.', N'Terrestrial planet', N'Inner Solar System', N'57,909,176', N'87.96', N'47.36', N'7.00487', N'0', N'2,439', N'7.5×10^7', N'3.302×10^23', N'5.427', N'58.64', N'0.0030', N'0.01', N'166.85')
INSERT INTO [dbo].[Planets] ([Name], [GeneralInfo], [Category], [Location], [AvgDistanceFromSun], [OrbitalPeriod], [AvgOrbitalSpeed], [Inclination], [Satellites], [Radius], [SurfaceArea], [Mass], [Density], [RotationPeriod], [RotationSpeed], [AxialTilt], [AvgSurfaceTemp]) VALUES (N'Neptune', N'Neptune is the eighth planet in the solar system. It is the smallest of the four gas giants and is classified in the Ice Giant subcategory because it is covered with a layer of water and ice. Neptune, is in orbital resonance with the dwarf planet Pluto.', N'Gas giant', N'Outer Solar System', N'4,498,252,900', N'60,190', N'5.432', N'1.76917', N'14', N'24,786', N'7.65×10^9', N'1.024×10^26', N'1.64', N'0.67', N'2.68', N'29.58', N'-212')
INSERT INTO [dbo].[Planets] ([Name], [GeneralInfo], [Category], [Location], [AvgDistanceFromSun], [OrbitalPeriod], [AvgOrbitalSpeed], [Inclination], [Satellites], [Radius], [SurfaceArea], [Mass], [Density], [RotationPeriod], [RotationSpeed], [AxialTilt], [AvgSurfaceTemp]) VALUES (N'Saturn', N'Saturn is the sixth planet farthest from the Sun and the second in a series of gaseous planets. Saturn is surrounded by planetary rings called Saturn''s rings, which contain mostly ice and dust. Because Saturn was the seventh planet in ancient times, the seventh day of the week was named Saturday.', N'Gas giant', N'Outer Solar System', N'1,426,725,413', N'10,832.327', N'9.639', N'2.488', N'82', N'60,268', N'4.27×10^10', N'5.8646×10^26', N'0.687', N'0.44', N'9.87', N'26.73', N'-130')
INSERT INTO [dbo].[Planets] ([Name], [GeneralInfo], [Category], [Location], [AvgDistanceFromSun], [OrbitalPeriod], [AvgOrbitalSpeed], [Inclination], [Satellites], [Radius], [SurfaceArea], [Mass], [Density], [RotationPeriod], [RotationSpeed], [AxialTilt], [AvgSurfaceTemp]) VALUES (N'Uranus', N'Uranus is the seventh planet far from the Sun. Uranus is one of the four gas giants and is classified in the ice giant sub-category. It is composed mainly of hydrogen and helium and in its center is a molten core of iron and silicates surrounded by a thick layer of ice, methane and ammonia. Beyond the solid layers lies the thick atmosphere composed of hydrogen and helium.', N'Gas giant', N'Outer Solar System', N'2,870,972,220', N'30,799.095', N'6.795', N'0.76986', N'27', N'25,559', N'8.13×10^9', N'8.686×10^25', N'1.29', N'0.72', N'2.59', N'97.86', N'-220')
INSERT INTO [dbo].[Planets] ([Name], [GeneralInfo], [Category], [Location], [AvgDistanceFromSun], [OrbitalPeriod], [AvgOrbitalSpeed], [Inclination], [Satellites], [Radius], [SurfaceArea], [Mass], [Density], [RotationPeriod], [RotationSpeed], [AxialTilt], [AvgSurfaceTemp]) VALUES (N'Venus', N'Venus is the second planet far from the Sun. Venus'' orbit is the closest to Earth''s orbit, and its size is close to Earth''s size. The most striking feature of Venus is the immense heat that prevails on its surface, more than any other planet in the solar system.', N'Terrestrial planet', N'Inner Solar System', N'108,208,926', N'224.7', N'35.02', N'3.39471', N'0', N'6,052', N'4.6×10^8', N'4.8685×10^24', N'5.204', N'117', N'0.0018', N'2.64', N'436.8')
